import React from 'react';

function Candle({ animationDelay }) {
  return (
    <div className="candle" style={{ animationDelay }}>
      <div className="flame"></div>
      <div className="glow"></div>
      <div className="wax-drip"></div>
    </div>
  );
}

export default Candle;
