import React, { useRef, useEffect, useState } from 'react';

class Particle {
  constructor(canvas) {
    this.canvas = canvas;
    this.x = Math.random() * canvas.width;
    this.y = Math.random() * canvas.height;
    this.size = Math.random() * 3 + 1; // Size between 1 and 4
    this.speedX = Math.random() * 0.5 - 0.25; // Speed between -0.25 and 0.25
    this.speedY = Math.random() * 0.5 - 0.25;
    this.baseColor = [252, 209, 22]; // RGB for #FCD116
    this.maxOpacity = Math.random() * 0.5 + 0.5; // Max opacity between 0.5 and 1
    this.opacity = this.maxOpacity; // Start at max opacity
  }

  update(fadeOutProgress) {
    this.x += this.speedX;
    this.y += this.speedY;

    if (this.x < 0 || this.x > this.canvas.width) this.speedX *= -1;
    if (this.y < 0 || this.y > this.canvas.height) this.speedY *= -1;

    // Adjust opacity based on fade-out progress
    this.opacity = this.maxOpacity * (1 - fadeOutProgress);
  }

  draw(ctx) {
    const [r, g, b] = this.baseColor;
    ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${this.opacity})`;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

function ParticleCanvas() {
  const canvasRef = useRef(null);
  const [fadeOutProgress, setFadeOutProgress] = useState(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let particles = [];
    const fadeOutDuration = 2000; // 2 seconds for fade-out
    let fadeOutStartTime;

    const createParticles = () => {
      particles = [];
      const particleCount = 50; // Adjust this number to increase/decrease particles
      for (let i = 0; i < particleCount; i++) {
        particles.push(new Particle(canvas));
      }
    };

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      createParticles();
    };

    const animate = (timestamp) => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      if (fadeOutStartTime && timestamp - fadeOutStartTime < fadeOutDuration) {
        const progress = (timestamp - fadeOutStartTime) / fadeOutDuration;
        setFadeOutProgress(progress);
      } else if (fadeOutStartTime && timestamp - fadeOutStartTime >= fadeOutDuration) {
        setFadeOutProgress(1); // Ensure we reach full fade-out
        return; // Stop animation
      }

      particles.forEach(particle => {
        particle.update(fadeOutProgress);
        particle.draw(ctx);
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    animationFrameId = requestAnimationFrame(animate);

    // Set a timeout to start fade-out after 10 seconds
    const fadeOutTimeoutId = setTimeout(() => {
      fadeOutStartTime = performance.now();
    }, 500);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrameId);
      clearTimeout(fadeOutTimeoutId);
    };
  }, []);

  return <canvas ref={canvasRef} id="particle-canvas" />;
}

export default ParticleCanvas;
