import React, { useRef, useEffect, useState } from 'react';

class Particle {
  constructor(canvas) {
    this.canvas = canvas;
    this.x = Math.random() * canvas.width;
    this.y = Math.random() * canvas.height;
    this.size = Math.random() * 3 + 1; // Size between 1 and 4
    this.speedX = Math.random() * 0.5 - 0.25; // Speed between -0.25 and 0.25
    this.speedY = Math.random() * 0.5 - 0.25;
    this.baseColor = [252, 209, 22]; // RGB for #FCD116
    this.opacity = 0;
  }

  update() {
    this.x += this.speedX;
    this.y += this.speedY;

    if (this.x < 0 || this.x > this.canvas.width) this.speedX *= -1;
    if (this.y < 0 || this.y > this.canvas.height) this.speedY *= -1;

    if (this.opacity < 1) {
      this.opacity += 0.02; // Adjust this value to change fade-in speed
    }
  }

  draw(ctx) {
    const [r, g, b] = this.baseColor;
    ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${this.opacity * (Math.random() * 0.5 + 0.5)})`;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

function ParticleCanvas() {
  const canvasRef = useRef(null);
  const [showParticles, setShowParticles] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let particles = [];

    const createParticles = () => {
      particles = [];
      const particleCount = 200; // Adjust this number to increase/decrease particles
      for (let i = 0; i < particleCount; i++) {
        particles.push(new Particle(canvas));
      }
    };

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      createParticles();
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      if (showParticles) {
        particles.forEach(particle => {
          particle.update();
          particle.draw(ctx);
        });
      }
      animationFrameId = requestAnimationFrame(animate);
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    animate();

    // Set a timeout to show particles after 10 seconds
    const timeoutId = setTimeout(() => {
      setShowParticles(true);
    }, 10000);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrameId);
      clearTimeout(timeoutId);
    };
  }, [showParticles]);

  return <canvas ref={canvasRef} id="particle-canvas" />;
}

export default ParticleCanvas;
