import React, { useRef, useEffect } from 'react';

class Particle {
  constructor(canvas, fadeOutDelay) {
    this.canvas = canvas;
    this.x = Math.random() * canvas.width;
    this.y = Math.random() * canvas.height;
    this.baseSize = Math.random() * 3 + 1; // Base size between 1 and 4
    this.size = 0; // Start at size 0 and grow
    this.speedX = Math.random() * 0.5 - 0.25; // Speed between -0.25 and 0.25
    this.speedY = Math.random() * 0.5 - 0.25;
    this.color = `rgba(252, 209, 22, ${Math.random() * 0.5 + 0.5})`;
    this.fadeOutDelay = fadeOutDelay;
    this.fadeOutStart = null;
    this.fadeOutDuration = 2000; // 2 seconds for fade-out
    this.state = 'growing'; // 'growing', 'stable', or 'shrinking'
  }

  update(currentTime) {
    this.x += this.speedX;
    this.y += this.speedY;

    if (this.x < 0 || this.x > this.canvas.width) this.speedX *= -1;
    if (this.y < 0 || this.y > this.canvas.height) this.speedY *= -1;

    if (this.state === 'growing') {
      this.size = Math.min(this.baseSize, this.size + 0.1);
      if (this.size === this.baseSize) this.state = 'stable';
    } else if (this.state === 'stable' && currentTime >= this.fadeOutDelay) {
      this.state = 'shrinking';
      this.fadeOutStart = currentTime;
    } else if (this.state === 'shrinking') {
      const elapsed = currentTime - this.fadeOutStart;
      const progress = Math.min(elapsed / this.fadeOutDuration, 1);
      this.size = this.baseSize * (1 - progress);
    }
  }

  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }

  isVisible() {
    return this.size > 0;
  }
}

function ParticleCanvas() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    let particles = [];
    const particleCount = 150;
    const displayDuration = 800; // 5 seconds

    const createParticles = () => {
      particles = [];
      for (let i = 0; i < particleCount; i++) {
        const fadeOutDelay = displayDuration + (i / particleCount) * 2000; // Stagger fade-out over 2 seconds
        particles.push(new Particle(canvas, fadeOutDelay));
      }
    };

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      createParticles();
    };

    const animate = (timestamp) => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles = particles.filter(particle => particle.isVisible());
      particles.forEach(particle => {
        particle.update(timestamp);
        particle.draw(ctx);
      });

      if (particles.length > 0) {
        animationFrameId = requestAnimationFrame(animate);
      }
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    animationFrameId = requestAnimationFrame(animate);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return <canvas ref={canvasRef} id="particle-canvas" />;
}

export default ParticleCanvas;
