import React, { useState } from 'react';
import CandlesContainer from './CandlesContainer';
import NamesContainer from './NamesContainer';

const MemorialContainer = () => {
  const [showLighting, setShowLighting] = useState(false);

  const handleLightCandle = () => {
    setShowLighting(true);
    setTimeout(() => setShowLighting(false), 500);
  };

  return (
    <div className="memorial-container">
      <div className="overlay"></div>
      <div className="content">
        <h1>Kumbuka</h1>
        <p className="subtitle">Remembering Innocent Lives Lost in Eastern Congo</p>
        <button className="cta-button" onClick={handleLightCandle}>Light a Candle</button>
        <a href="memorialWall.html" className="cta-button">Kumbuka Memorial</a>
      </div>
      <CandlesContainer onLightCandle={handleLightCandle} />
      <NamesContainer />
      <div className={`candle-lighting ${showLighting ? 'active' : ''}`}></div>
    </div>
  );
};

export default MemorialContainer;
