import React, { useState, useEffect } from 'react';
import Candle from './Candle';

const CandlesContainer = ({ onLightCandle }) => {
  const [candles, setCandles] = useState([]);

  useEffect(() => {
    setCandles(Array.from({ length: 15 }, (_, i) => ({
      id: i,
      animationDelay: `${Math.random() * 2}s`,
      flameDuration: `${0.4 + Math.random() * 0.2}s`,
      waxDripDelay: `${Math.random() * 5}s`
    })));
  }, []);

  const addCandle = () => {
    setCandles(prevCandles => [
      ...prevCandles,
      {
        id: prevCandles.length,
        animationDelay: '0s',
        flameDuration: `${0.4 + Math.random() * 0.2}s`,
        waxDripDelay: '0s'
      }
    ]);
    onLightCandle();
  };

  return (
    <div className="candles-container">
      {candles.map(candle => (
        <Candle key={candle.id} {...candle} />
      ))}
      <button className="cta-button" onClick={addCandle}>Light a Candle</button>
    </div>
  );
};

export default CandlesContainer;
